CREATE TABLE `todolist`.`todos` (`id` INT NOT NULL AUTO_INCREMENT COMMENT 'タスクID' , `title` VARCHAR(255) NOT NULL COMMENT 'タスクタイトル' , `description` TEXT NULL COMMENT 'タスク説明' , `status` INT NOT NULL COMMENT 'ステータス(0=invalid,1=作業前,2=作業中,3=作業完了)' , `priority` INT NOT NULL COMMENT '優先度(0=invalid,1=低,2=中,3=高)' , `due_date` DATE NULL COMMENT '締切日' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '作成日' , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新日' , `s_deleted` BOOLEAN NOT NULL COMMENT '削除フラグ' , `category` INT NOT NULL COMMENT 'カテゴリー(0=invalid,1=tech,2=php,3=資格)' , `tags` VARCHAR(255) NULL COMMENT 'タグ' , `completed_at` TIMESTAMP NOT NULL COMMENT '完了日時' , PRIMARY KEY (`id`)) ENGINE = InnoDB COMMENT = 'タスクテーブル';
